using System;using System.Collections.Generic;using System.IO;using System.Linq;using System.Text;using System.Threading.Tasks;namespace ArrayToCsv{    class CseFileArray    {        static void Main(string[] args)        {            string[,] files = new string[2, 5];            files[0, 0] = "I ";            files[0, 1] = "am ";            files[0, 2] = "From ";            files[0, 3] = "davangere ";            files[0, 4] = "Karnataka ";            files[1, 0] = "vikki ";            files[1, 1] = "davangere ";            files[1, 2] = "bapuji ";            files[1, 3] = "karnataka ";            files[1, 4] = "57005";            StreamWriter sw = File.CreateText("Notes.csv");            for (int i = 0; i < 2; i++)            {                for (int j = 0; j < 5; j++)                {                    sw.Write(files[i, j] + ";");                }                sw.Write("\n");            }            sw.Flush();            sw.Close();            Console.WriteLine("done");        }    }}